css:
@main_color: [[main_color:#4b8e23]];
@menu_background_color: [[menu_background_color:#F0EFEA]];
@menu_border_color: [[menu_border_color:#AAAAAA]];
@menu_text_color: [[menu_text_color:#000]];

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background:-moz-linear-gradient(@top-color 0%, @bottom-color 100%); 
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background:-webkit-linear-gradient(@top-color 0%,@bottom-color 100%);  
	background:linear-gradient(@top-color 0%,@bottom-color 100%); 
}

#menu_class# {
  padding: 0;
  margin: 0;
  border: 0; }

#menu_class# ul, #menu_class# li {
  list-style: none;
  margin: 0;
  padding: 0; }

#menu_class# ul {
  position: relative;
  z-index: 597;
  float: left; }

#menu_class# ul li {
  float: left;
  min-height: 1px;
  line-height: 1.5em;
  vertical-align: middle; }

#menu_class# ul li.hover,
#menu_class# ul li:hover {
  position: relative;
  z-index: 599;
  cursor: default; }

#menu_class# ul ul {
  visibility: hidden;
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 598;
  width: 100%; }

#menu_class# ul ul li {
  float: none; }

#menu_class# ul ul, #menu_class# ul ul ul {
  top: -1px;
  left: 99%; }

#menu_class# ul li:hover > ul {
  visibility: visible; }

#menu_class# ul li {
  float: none; }

#menu_class# a {
  display: block; }

/* Custom CSS Styles */
#menu_class# {
  font-family: "Segoe UI", Arial, sans-serif;
  font-size: 11px; }

#menu_class#:after, #menu_class# ul:after {
    content: '';
    display: block;
    clear: both; }

#menu_class# ul {
    background: [[menu_background_color:#F0EFEA]];
    border: 1px solid @menu_border_color;
    padding: 4px;
    width: [[menu_width:200px]]; }

#menu_class# ul li {
      color: @menu_text_color;
      position: relative; 
}


#menu_class# ul li.hover, 
#menu_class# ul li:hover {
.linear-gradient (lighten(@main_color,10%), @main_color);
color: #FFF; }


#menu_class# ul li.hover > a, 
#menu_class# ul li:hover > a {
          color: #FFF;
          border: 1px solid @main_color; }

#menu_class# ul ul {width: [[sub_menu_width:150px]];}

#menu_class# a {
    border: 1px solid transparent;
    padding: 3px 10px; }

#menu_class# a:link, #menu_class# a:visited {
      color: @menu_text_color;
      text-decoration: none; }

#menu_class# a:hover {
.linear-gradient (lighten(@main_color,10%), @main_color);
color: #FFF; }

#menu_class# a:active {
      color: #ffa500; 
}
#menu_class# .has-sub:hover > a:after, 
#menu_class# .has-sub.hover > a:after {border-color: transparent transparent transparent #FFF; }

#menu_class# .has-sub > a:after {
    content: '';
    width: 0px;
    height: 0px;
    border-style: solid;
    border-width: 4px 0 4px 4px;
    border-color: transparent transparent transparent #808080;
    position: absolute;
    top: 50%;
    right: 5%;
    margin-top: -4px;
    -webkit-transform: rotate(360deg); }
